import React from "react";
import "../styles/submitButton.css"; // Import CSS for styling

const SubmitButton = ({ duration, selectedGenres }) => {
    const handleSubmit = () => {
        if (!duration || selectedGenres.length === 0) {
            alert("‚ö†Ô∏è Please enter a duration and select at least one genre!");
            return;
        }
        alert(`üé¨ Time: ${duration} min\nüìΩÔ∏è Genres: ${selectedGenres.join(", ")}`);
    };

    return (
        <div className="submit-container">
            <button className="submit-btn" onClick={handleSubmit}>üéØ Get Recommendations</button>
        </div>
    );
};

export default SubmitButton;
