import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/heroSection.css";

const allGenres = [
    "Action", "Comedy", "Drama", "Horror", "Romance",
    "Sci-Fi", "Thriller", "Animation", "Mystery", "Adventure",
    "Fantasy", "Crime", "Music", "Family", "War",
    "Documentary", "Western", "Superhero", "Historical", "Musical",
    "Biopic", "Sports", "Noir", "Psychological", "Experimental",
    "Anime", "Zombie", "Cyberpunk", "Martial Arts", "Dark Comedy",
    "Satire", "Spy", "Legal", "Political", "Medical Drama"
];

const HeroSection = () => {
    const navigate = useNavigate();
    const [duration, setDuration] = useState(0);
    const [selectedGenres, setSelectedGenres] = useState([]);
    const [movieDetails, setMovieDetails] = useState({
        voteAverage: "",
        voteCount: "",
        runtime: "",
        adult: false,
        popularity: "",
        numVotes: ""
    });

    // Handle Input Changes
    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setMovieDetails({
            ...movieDetails,
            [name]: type === "checkbox" ? checked : value
        });
    };

    // Handle Duration Change
    const handleHoursChange = (e) => {
        let hours = parseInt(e.target.value, 10);
        if (isNaN(hours) || hours < 0) hours = 0;
        if (hours > 12) hours = 12;
        const minutes = duration % 60;
        setDuration(hours * 60 + minutes);
    };

    const handleMinutesChange = (e) => {
        let minutes = parseInt(e.target.value, 10);
        if (isNaN(minutes) || minutes < 0) minutes = 0;
        if (minutes > 59) minutes = 59;
        const hours = Math.floor(duration / 60);
        setDuration(hours * 60 + minutes);
    };

    // Handle Genre Selection
    const handleGenreClick = (genre) => {
        if (!selectedGenres.includes(genre)) {
            setSelectedGenres([...selectedGenres, genre]);
        }
    };

    const removeGenre = (genre) => {
        setSelectedGenres(selectedGenres.filter(g => g !== genre));
    };

    // Handle Submit
    const handleSubmit = () => {
        if (duration < 1) {
            alert("âš ï¸ Please enter a valid movie duration (at least 1 minute)!");
            return;
        }

        if (selectedGenres.length === 0) {
            alert("âš ï¸ Please select at least one genre!");
            return;
        }

        alert(`ðŸŽ¬ Time: ${duration} min\nðŸ“½ï¸ Genres: ${selectedGenres.join(", ")}`);
    };

    return (
        <div className="hero-section">
            <div className="movie-poster">
                <img src="https://image.tmdb.org/t/p/original/s3TBrRGB1iav7gFOCNx3H31MoES.jpg" alt="Movie Poster" />
            </div>

            <div className="hero-content">
                <h1>ðŸŽ¬ Movie Recommendation</h1>

                {/* Duration Selector */}
                <div className="duration-container">
                    <h3>ðŸŽ¬ Select Movie Duration</h3>
                    <div className="duration-inputs">
                        <input type="number" value={Math.floor(duration / 60)} onChange={handleHoursChange} placeholder="Hours" className="time-input" />
                        <span className="separator">h</span>
                        <input type="number" value={duration % 60} onChange={handleMinutesChange} placeholder="Minutes" className="time-input" />
                        <span className="separator">min</span>
                    </div>
                </div>

                {/* Movie Details */}
                <div className="movie-info">
                    <input type="number" name="voteAverage" placeholder="Vote Average" value={movieDetails.voteAverage} onChange={handleInputChange} />
                    <input type="number" name="voteCount" placeholder="Vote Count" value={movieDetails.voteCount} onChange={handleInputChange} />
                    <input type="number" name="runtime" placeholder="Runtime (mins)" value={movieDetails.runtime} onChange={handleInputChange} />
                    <label className="checkbox-label">
                        <input type="checkbox" name="adult" checked={movieDetails.adult} onChange={handleInputChange} /> Adult Content
                    </label>
                    <input type="number" name="popularity" placeholder="Popularity" value={movieDetails.popularity} onChange={handleInputChange} />
                    <input type="number" name="numVotes" placeholder="Number of Votes" value={movieDetails.numVotes} onChange={handleInputChange} />
                </div>

                {/* Genre Selector */}
                <div className="genre-container">
                    <h3>Select Genres:</h3>
                    <div className="selected-genres">
                        {selectedGenres.map((genre, index) => (
                            <span key={index} className="selected-genre" onClick={() => removeGenre(genre)}>
                                {genre} âœ–
                            </span>
                        ))}
                    </div>
                    <div className="all-genres">
                        {allGenres.filter(genre => !selectedGenres.includes(genre)).map((genre, index) => (
                            <span key={index} className="genre" onClick={() => handleGenreClick(genre)}>
                                {genre}
                            </span>
                        ))}
                    </div>
                </div>

                {/* Submit & Predict Buttons */}
                <div className="submit-container">
                    <button className="submit-btn" onClick={handleSubmit}>ðŸŽ¯ Get Recommendations</button>
                </div>
            </div>
        </div>
    );
};

export default HeroSection;
