import React, { useState } from "react";
import TimeDuration from "./components/TimeDuration";
import GenreSelector from "./components/GenreSelector";
import "./styles.css";

function App() {
    const [duration, setDuration] = useState("");
    const [selectedGenres, setSelectedGenres] = useState([]);

    const handleSubmit = () => {
        console.log("Selected Time:", duration);
        console.log("Selected Genres:", selectedGenres);
        alert(`Time: ${duration} min\nGenres: ${selectedGenres.join(", ")}`);
    };

    return (
        <div className="container">
            <h1>Movie Recommendation</h1>

            {/* ðŸŽ¬ Time Duration & Genre Selection */}
            <div className="selection-container">
                <TimeDuration duration={duration} setDuration={setDuration} />
                <GenreSelector selectedGenres={selectedGenres} setSelectedGenres={setSelectedGenres} />
            </div>

            {/* ðŸŽ¯ Submit Button */}
            <button className="submit-btn" onClick={handleSubmit}>Submit</button>
        </div>
    );
}

export default App;
