import React, { useState } from "react";
import "../styles/HeroSection.css"; // Import the CSS file

const HeroSection = ({ movie, genres, languages }) => {
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [selectedLanguages, setSelectedLanguages] = useState([]);
  const [duration, setDuration] = useState({ hours: "", minutes: "" });

  if (!movie) {
    return <div className="text-center">Loading...</div>;
  }

  const { title, overview, vote_average, vote_count, adult, budget, popularity, spoken_languages, numVotes, backdrop_path } = movie;

  // Handle Genre Selection
  const addGenre = (genre) => {
    if (!selectedGenres.includes(genre)) {
      setSelectedGenres([...selectedGenres, genre]);
    }
  };

  const removeGenre = (genre) => {
    setSelectedGenres(selectedGenres.filter((g) => g !== genre));
  };

  // Handle Language Selection
  const addLanguage = (language) => {
    if (!selectedLanguages.includes(language)) {
      setSelectedLanguages([...selectedLanguages, language]);
    }
  };

  const removeLanguage = (language) => {
    setSelectedLanguages(selectedLanguages.filter((l) => l !== language));
  };

  // Handle Duration Input
  const handleDurationChange = (e) => {
    setDuration({ ...duration, [e.target.name]: e.target.value });
  };

  return (
    <div className="hero-section">
      {/* Background Image */}
      <div
        className="background"
        style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original${backdrop_path})` }}
      ></div>

      {/* Movie Content */}
      <div className="hero-content">
        <h1 className="hero-title">{title}</h1>
        <p className="hero-description">{overview}</p>

        {/* Movie Details */}
        <div className="movie-details">
          <p><span>Rating:</span> {vote_average} / 10</p>
          <p><span>Votes:</span> {vote_count}</p>
          <p><span>Adult:</span> {adult ? "Yes" : "No"}</p>
          <p><span>Budget:</span> ${budget}</p>
          <p><span>Popularity:</span> {popularity}</p>
          <p><span>Num Votes:</span> {numVotes}</p>
        </div>

        {/* Genre Selector */}
        <div className="genre-container">
          <h3>Select Genres:</h3>
          <div className="genre-list">
            {selectedGenres.map((genre) => (
              <span key={genre} className="genre-item" onClick={() => removeGenre(genre)}>
                {genre} ‚ùå
              </span>
            ))}
          </div>
          <select onChange={(e) => addGenre(e.target.value)}>
            <option value="">Choose Genre</option>
            {genres.map((genre) => (
              <option key={genre} value={genre}>{genre}</option>
            ))}
          </select>
        </div>

        {/* Language Selector */}
        <div className="language-container">
          <h3>Select Languages:</h3>
          <div className="language-list">
            {selectedLanguages.map((lang) => (
              <span key={lang} className="language-item" onClick={() => removeLanguage(lang)}>
                {lang} ‚ùå
              </span>
            ))}
          </div>
          <select onChange={(e) => addLanguage(e.target.value)}>
            <option value="">Choose Language</option>
            {spoken_languages.map((lang, index) => (
              <option key={index} value={lang.name}>{lang.name}</option>
            ))}
          </select>
        </div>

        {/* Duration Selector */}
        <div className="duration-container">
          <h3>Set Duration:</h3>
          <div>
            <input type="number" name="hours" value={duration.hours} onChange={handleDurationChange} placeholder="Hours" />
            <input type="number" name="minutes" value={duration.minutes} onChange={handleDurationChange} placeholder="Minutes" />
          </div>
        </div>

        {/* Buttons */}
        <div className="buttons-container">
          <button onClick={() => (window.location.href = "/predict")} className="predict-btn">
            Predict Rating üé¨
          </button>
          <button className="submit-btn">Submit üöÄ</button>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
