import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import TimeDuration from "./components/TimeDuration";
import GenreSelector from "./components/GenreSelector";
import SubmitButton from "./components/SubmitButton";
import Navbar from "./components/Navbar";
import TrendingMovies from "./components/TrendingMovies"; // Import Trending Movies Page
import Awards from "./components/Awards"; // Import Awards Page
import "./styles/app.css"; // Import Global Styles

const App = () => {
    const [timeDuration, setTimeDuration] = useState(0);
    const [selectedGenres, setSelectedGenres] = useState([]);

    return (
        <Router>
            <div className="app-container">
                <Navbar /> {/* Navbar is fixed and always visible */}

                <Routes>
                    {/* Home Page */}
                    <Route path="/" element={
                        <>
                            <h1>ðŸŽ¬ Movie Recommendation</h1>
                            <TimeDuration duration={timeDuration} setDuration={setTimeDuration} />
                            <GenreSelector selectedGenres={selectedGenres} setSelectedGenres={setSelectedGenres} />
                            <SubmitButton duration={timeDuration} selectedGenres={selectedGenres} />
                        </>
                    } />

                    {/* Trending Movies Page */}
                    <Route path="/trending" element={<TrendingMovies />} />

                    {/* Awards Page */}
                    <Route path="/awards" element={<Awards />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
