import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Navbar from "./components/Navbar";
import HeroSection from "./components/HeroSection";
import TrendingMovies from "./components/TrendingMovies";
import Awards from "./components/Awards";
import PredictRating from "./components/PredictRating"; // Ensure this component exists
import "./styles/app.css";

const genresList = [
    "Action", "Adventure", "Animation", "Comedy", "Crime", "Drama",
    "Fantasy", "Horror", "Mystery", "Romance", "Sci-Fi", "Thriller"
];

const App = () => {
    const [movie, setMovie] = useState({
        title: "Inception",
        overview: "A thief who enters the dreams of others to steal secrets must plant an idea in a target’s mind.",
        backdrop_path: "/s3TBrRGB1iav7gFOCNx3H31MoES.jpg",
    });

    return (
        <Router>
            <div className="app-container">
                <Navbar />

                <Routes>
                    {/* Home Page */}
                    <Route path="/" element={<HeroSection movie={movie} genres={genresList} />} />

                    {/* Trending Movies Page */}
                    <Route path="/trending" element={<TrendingMovies />} />

                    {/* Awards Page */}
                    <Route path="/awards" element={<Awards />} />

                    {/* Predict Rating Page */}
                    <Route path="/predict" element={<PredictRating />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
