import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import TimeDuration from "./components/TimeDuration";
import GenreSelector from "./components/GenreSelector";
import SubmitButton from "./components/SubmitButton"; // New Submit Button Component
import "./styles/app.css"; // Import Global Styles

const App = () => {
    const [timeDuration, setTimeDuration] = useState("");
    const [selectedGenres, setSelectedGenres] = useState([]);

    return (
        <Router>
            <div className="app-container">
                <h1>ðŸŽ¬ Movie Recommendation</h1>
                <Routes>
                    <Route path="/" element={
                        <>
                            <TimeDuration duration={timeDuration} setDuration={setTimeDuration} />
                            <GenreSelector selectedGenres={selectedGenres} setSelectedGenres={setSelectedGenres} />
                            <SubmitButton timeDuration={timeDuration} selectedGenres={selectedGenres} />
                        </>
                    } />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
