import React from "react";
import "../styles/timeDuration.css"; // Import separate CSS

const TimeDuration = ({ duration, setDuration }) => {
    const handleHoursChange = (e) => {
        let hours = parseInt(e.target.value, 10);
        if (isNaN(hours) || hours < 0) hours = 0;  // Prevent negative values
        if (hours > 12) hours = 12;  // Max 12 hours

        const minutes = duration % 60; // Keep existing minutes
        setDuration(hours * 60 + minutes); // Convert to minutes and update state
    };

    const handleMinutesChange = (e) => {
        let minutes = parseInt(e.target.value, 10);
        if (isNaN(minutes) || minutes < 0) minutes = 0;  // Prevent negative values
        if (minutes > 59) minutes = 59;  // Max 59 minutes

        const hours = Math.floor(duration / 60); // Keep existing hours
        setDuration(hours * 60 + minutes);
    };

    return (
        <div className="duration-container">
            <h3>ðŸŽ¬ Select Movie Duration</h3>
            <div className="duration-inputs">
                <input
                    type="number"
                    value={Math.floor(duration / 60)}
                    onChange={handleHoursChange}
                    placeholder="Hours"
                    className="time-input"
                />
                <span className="separator">h</span>
                <input
                    type="number"
                    value={duration % 60}
                    onChange={handleMinutesChange}
                    placeholder="Minutes"
                    className="time-input"
                />
                <span className="separator">min</span>
            </div>
        </div>
    );
};

export default TimeDuration;
