import React from "react";
import GenreLanguageSelector from "./GenreLanguageSelector";
import "../styles/MainContent.css";

const MainContent = () => {
  return (
    <div className="main-content">
      <div className="left-box">
        <img src="/images/background.j" alt="Movie Rating" className="movie-image" />
      </div>
      <div className="right-box">
        <h2>Movie Details</h2>
        <p><strong>Vote Average:</strong> 8.5</p>
        <p><strong>Vote Count:</strong> 12000</p>
        <p><strong>Runtime:</strong> 2h 20min</p>
        <p><strong>Adult:</strong> No</p>
        <p><strong>Budget:</strong> $150M</p>
        <p><strong>Popularity:</strong> 95%</p>
        <GenreLanguageSelector />
      </div>
    </div>
  );
};

export default MainContent;
