import React from "react";
import "../styles/submitButton.css"; // Import CSS for styling

const SubmitButton = ({ duration, selectedGenres }) => {
    const handleSubmit = () => {
        // Convert duration to number (if it's a string)
        const time = Number(duration);

        // Validate duration (should be a number and greater than zero)
        if (isNaN(time) || time <= 0) {
            alert("‚ö†Ô∏è Please enter a valid movie duration (at least 1 minute)!");
            return;
        }
        
        // Validate maximum movie duration (12 hours = 720 minutes)
        if (time > 720) {
            alert("‚ö†Ô∏è Maximum allowed duration is 12 hours (720 minutes)!");
            return;
        }

        // Ensure at least one genre is selected
        if (selectedGenres.length === 0) {
            alert("‚ö†Ô∏è Please select at least one genre!");
            return;
        }

        // If all validations pass, show selected details
        alert(`üé¨ Time: ${time} min\nüìΩÔ∏è Genres: ${selectedGenres.join(", ")}`);
    };

    return (
        <div className="submit-container">
            <button className="submit-btn" onClick={handleSubmit}>üéØ Get Recommendations</button>
        </div>
    );
};

export default SubmitButton;
