import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/heroSection.css";

const allGenres = [
    "Action", "Comedy", "Drama", "Horror", "Romance",
    "Sci-Fi", "Thriller", "Animation", "Mystery", "Adventure",
    "Fantasy", "Crime", "Music", "Family", "War",
    "Documentary", "Western", "Superhero", "Historical", "Musical",
    "Biopic", "Sports", "Noir", "Psychological", "Experimental",
    "Anime", "Zombie", "Cyberpunk", "Martial Arts", "Dark Comedy",
    "Satire", "Spy", "Legal", "Political", "Medical Drama"
];

const HeroSection = () => {
    const navigate = useNavigate();
    const [duration, setDuration] = useState(0);
    const [selectedGenres, setSelectedGenres] = useState([]);
    const [movieDetails, setMovieDetails] = useState({
        username: "",
        email: "",
        age: "",
        voteAverage: "",
        voteCount: "",
        runtime: "",
        adult: false,
        popularity: "",
        numVotes: ""
    });

    const movie = {
        title: "Inception",
        overview: "A thief who enters the dreams of others to steal secrets must plant an idea in a target’s mind.",
        backdrop_path: "/s3TBrRGB1iav7gFOCNx3H31MoES.jpg",
        vote_average: 8.8,
        vote_count: 2100000,
        release_date: "2010-07-16",
        runtime: 148,
        adult: false,
        popularity: 92.5
    };

    const handleGenreClick = (genre) => {
        if (!selectedGenres.includes(genre)) {
            setSelectedGenres([...selectedGenres, genre]);
        }
    };

    const removeGenre = (genre) => {
        setSelectedGenres(selectedGenres.filter(g => g !== genre));
    };

    const handleInputChange = (e) => {
        setMovieDetails({
            ...movieDetails,
            [e.target.name]: e.target.value,
        });
    };

    const handleDurationChange = (e) => {
        setDuration(e.target.value);
    };

    const handleSubmit = () => {
        if (!movieDetails.username || !movieDetails.email || !movieDetails.age) {
            alert("⚠️ Please enter all credentials!");
            return;
        }

        if (!movieDetails.voteAverage || !movieDetails.voteCount || !movieDetails.runtime || !movieDetails.popularity || !movieDetails.numVotes) {
            alert("⚠️ Please enter all movie details!");
            return;
        }

        alert(`🎬 Submitted details:
        - Username: ${movieDetails.username}
        - Vote Average: ${movieDetails.voteAverage}
        - Vote Count: ${movieDetails.voteCount}
        - Runtime: ${movieDetails.runtime} minutes
        - Adult: ${movieDetails.adult ? "Yes" : "No"}
        - Popularity: ${movieDetails.popularity}
        - Number of Votes: ${movieDetails.numVotes}`);
    };

    return (
        <div className="hero-section">
            <div className="input-section">
                <h2>Enter Your Details</h2>
                <input type="text" name="username" placeholder="Username" value={movieDetails.username} onChange={handleInputChange} />
                <input type="email" name="email" placeholder="Email" value={movieDetails.email} onChange={handleInputChange} />
                <input type="number" name="age" placeholder="Age" value={movieDetails.age} onChange={handleInputChange} />
            </div>

            <div className="movie-details">
                <h3>Movie Details</h3>
                <input type="number" name="voteAverage" placeholder="Vote Average" value={movieDetails.voteAverage} onChange={handleInputChange} />
                <input type="number" name="voteCount" placeholder="Vote Count" value={movieDetails.voteCount} onChange={handleInputChange} />
                <input type="number" name="runtime" placeholder="Runtime (minutes)" value={movieDetails.runtime} onChange={handleInputChange} />
                <label>
                    <input type="checkbox" name="adult" checked={movieDetails.adult} onChange={() => setMovieDetails({...movieDetails, adult: !movieDetails.adult})} />
                    Adult (18+)
                </label>
                <input type="number" name="popularity" placeholder="Popularity" value={movieDetails.popularity} onChange={handleInputChange} />
                <input type="number" name="numVotes" placeholder="Number of Votes" value={movieDetails.numVotes} onChange={handleInputChange} />
            </div>

            <div className="genre-container">
                <h3>Select Genres:</h3>
                <div className="selected-genres">
                    {selectedGenres.map((genre, index) => (
                        <span key={index} className="selected-genre" onClick={() => removeGenre(genre)}>
                            {genre} ✖
                        </span>
                    ))}
                </div>
                <div className="all-genres">
                    {allGenres.filter(genre => !selectedGenres.includes(genre)).map((genre, index) => (
                        <span key={index} className="genre" onClick={() => handleGenreClick(genre)}>
                            {genre}
                        </span>
                    ))}
                </div>
            </div>

            <div className="submit-container">
                <button className="submit-btn" onClick={handleSubmit}>🎯 Get Recommendations</button>
                <button className="predict-btn" onClick={() => navigate("/predict")}>🔮 Predict Rating</button>
            </div>
        </div>
    );
};

export default HeroSection;
